INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (1,17,'EF00MA','Não Identificado','Não Identificado',2,10),
	 (2,2,'EF01MA01','Contagem de rotina, contagem ascendente e descendente, reconhecimento de números no contexto diário: indicação de quantidades, indicação de ordem ou indicação de código para a organização de informações','Utilizar números naturais como indicador de quantidade ou de ordem em diferentes situações cotidianas e reconhecer situações em que os números não indicam contagem nem ordem, mas sim código de identificação.',2,5),
	 (3,2,'EF01MA02','Quantificação de elementos de uma coleção: estimativas, contagem um a um, pareamento ou outros agrupamentos e comparação','Contar de maneira exata ou aproximada, utilizando diferentes estratégias como o pareamento e outros agrupamentos.',2,5),
	 (4,2,'EF01MA03','Quantificação de elementos de uma coleção: estimativas, contagem um a um, pareamento ou outros agrupamentos e comparação','Estimar e comparar quantidades de objetos de dois conjuntos (em torno de 20 elementos), por estimativa e/ou por correspondência (um a um, dois a dois) para indicar tem mais, tem menos ou tem a mesma quantidade.',2,5),
	 (5,2,'EF01MA04','Leitura, escrita e comparação de números naturais (até 100), reta numérica','Contar a quantidade de objetos de coleções até 100 unidades e apresentar o resultado por registros verbais e simbólicos, em situações de seu interesse, como jogos, brincadeiras, materiais da sala de aula, entre outros.',2,5),
	 (6,2,'EF01MA05','Leitura, escrita e comparação de números naturais (até 100), reta numérica','Comparar números naturais de até duas ordens em situações cotidianas, com e sem suporte da reta numérica.',2,5),
	 (7,2,'EF01MA06','Construção de fatos básicos da adição','Construir fatos básicos da adição e utilizá-los em procedimentos de cálculo para resolver problemas.',2,5),
	 (8,2,'EF01MA07','Composição e decomposição de números naturais','Compor e decompor número de até duas ordens, por meio de diferentes adições, com o suporte de material manipulável, contribuindo para a compreensão de características do sistema de numeração decimal e o desenvolvimento de estratégias de cálculo.',2,5),
	 (9,2,'EF01MA08','Problemas envolvendo diferentes significados da adição e da subtração (juntar, acrescentar, separar, retirar)','Resolver e elaborar problemas de adição e de subtração, envolvendo números de até dois algarismos, com os significados de juntar, acrescentar, separar e retirar, com o suporte de imagens e/ou material manipulável, utilizando estratégias e formas de registro pessoais.',2,5),
	 (10,2,'EF01MA09','Padrões figurais e numéricos: investigação de regularidades ou padrões em sequências','Organizar e ordenar objetos familiares ou representações por figuras, por meio de atributos, tais como cor, forma e medida.',2,6);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (11,2,'EF01MA10','Sequências recursivas: observação de regras usadas utilizadas em seriações numéricas (mais 1, mais 2, menos 1, menos 2, por exemplo)','Descrever, após o reconhecimento e a explicitação de um padrão (ou regularidade), os elementos ausentes em sequências recursivas de números naturais, objetos ou figuras.',2,6),
	 (12,2,'EF01MA11','Localização de objetos e de pessoas no espaço, utilizando diversos pontos de referência e vocabulário apropriado','Descrever a localização de pessoas e de objetos no espaço em relação à sua própria posição, utilizando termos como à direita, à esquerda, em frente, atrás.',2,7),
	 (13,2,'EF01MA12','Localização de objetos e de pessoas no espaço, utilizando diversos pontos de referência e vocabulário apropriado','Descrever a localização de pessoas e de objetos no espaço segundo um dado ponto de referência, compreendendo que, para a utilização de termos que se referem à posição, como direita, esquerda, em cima, em baixo, é necessário explicitar-se o referencial.',2,7),
	 (14,2,'EF01MA13','Figuras geométricas espaciais: reconhecimento e relações com objetos familiares do mundo físico','Relacionar figuras geométricas espaciais (cones, cilindros, esferas e blocos retangulares) a objetos familiares do mundo físico.',2,7),
	 (15,2,'EF01MA14','Figuras geométricas planas: reconhecimento do formato das faces de figuras geométricas espaciais','Identificar e nomear figuras planas (círculo, quadrado, retângulo e triângulo) em desenhos apresentados em diferentes disposições ou em contornos de faces de sólidos geométricos.',2,7),
	 (16,2,'EF01MA15','Medidas de comprimento, massa e capacidade: comparações e unidades de medida não convencionais','Comparar comprimentos, capacidades ou massas, utilizando termos como mais alto, mais baixo, mais comprido, mais curto, mais grosso, mais fino, mais largo, mais pesado, mais leve, cabe mais, cabe menos, entre outros, para ordenar objetos de uso cotidiano.',2,8),
	 (17,2,'EF01MA16','Medidas de tempo: unidades de medida de tempo, suas relações e o uso do calendário','Relatar em linguagem verbal ou não verbal sequência de acontecimentos relativos a um dia, utilizando, quando possível, os horários dos eventos.',2,8),
	 (18,2,'EF01MA17','Medidas de tempo: unidades de medida de tempo, suas relações e o uso do calendário','Reconhecer e relacionar períodos do dia, dias da semana e meses do ano, utilizando calendário, quando necessário.',2,8),
	 (19,2,'EF01MA18','Medidas de tempo: unidades de medida de tempo, suas relações e o uso do calendário','Produzir a escrita de uma data, apresentando o dia, o mês e o ano, e indicar o dia da semana de uma data, consultando calendários.',2,8),
	 (20,2,'EF01MA19','Sistema monetário brasileiro: reconhecimento de cédulas e moedas','Reconhecer e relacionar valores de moedas e cédulas do sistema monetário brasileiro para resolver situações simples do cotidiano do estudante.',2,8);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (21,2,'EF01MA20','Noção de acaso','Classificar eventos envolvendo o acaso, tais como acontecerá com certeza, talvez aconteça e é impossível acontecer, em situações do cotidiano.',2,9),
	 (22,2,'EF01MA21','Leitura de tabelas e de gráficos de colunas simples','Ler dados expressos em tabelas e em gráficos de colunas simples.',2,9),
	 (23,2,'EF01MA22','Coleta e organização de informações, racionais registros pessoais para comunicação de informações coletadas','Realizar pesquisa, envolvendo até duas variáveis categóricas de seu interesse e universo de até 30 elementos, e organizar dados por meio de representações pessoais.',2,9),
	 (24,3,'EF02MA01','Leitura, escrita, comparação e ordenação de números de até três ordens pela compreensão de características do sistema de numeração decimal (valor posicional e papel do zero)','Comparar e ordenar números naturais (até a ordem de centenas) pela compreensão de características do sistema de numeração decimal (valor posicional e função do zero).',2,5),
	 (25,3,'EF02MA02','Leitura, escrita, comparação e ordenação de números de até três ordens pela compreensão de características do sistema de numeração decimal (valor posicional e papel do zero)','Fazer estimativas por meio de estratégias diversas a respeito da quantidade de objetos de coleções e registrar o resultado da contagem desses objetos (até 1000 unidades).',2,5),
	 (26,3,'EF02MA03','Leitura, escrita, comparação e ordenação de números de até três ordens pela compreensão de características do sistema de numeração decimal (valor posicional e papel do zero)','Comparar quantidades de objetos de dois conjuntos, por estimativa e/ou por correspondência (um a um, dois a dois, entre outros), para indicar tem mais, tem menos ou tem a mesma quantidade, indicando, quando for o caso, quantos a mais e quantos a menos.',2,5),
	 (27,3,'EF02MA04','Composição e decomposição de números naturais (até 1000)','Compor e decompor números naturais de até três ordens, com suporte de material manipulável, por meio de diferentes adições.',2,5),
	 (28,3,'EF02MA05','Construção de fatos fundamentais da adição e da subtração','Construir fatos básicos da adição e subtração e utilizá-los no cálculo mental ou escrito.',2,5),
	 (29,3,'EF02MA06','Problemas envolvendo diferentes significados da adição e da subtração (juntar, acrescentar, separar, retirar)','Resolver e elaborar problemas de adição e de subtração, envolvendo números de até três ordens, com os significados de juntar, acrescentar, separar, retirar, utilizando estratégias pessoais ou convencionais.',2,5),
	 (30,3,'EF02MA07','Problemas envolvendo adição de parcelas iguais (multiplicação)','Resolver e elaborar problemas de multiplicação (por 2, 3, 4 e 5) com a ideia de adição de parcelas iguais por meio de estratégias e formas de registro pessoais, utilizando ou não suporte de imagens e/ou material manipulável.',2,5);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (31,3,'EF02MA08','Problemas envolvendo significados de dobro, metade, triplo e terça parte','Resolver e elaborar problemas envolvendo dobro, metade, triplo e terça parte, com o suporte de imagens ou material manipulável, utilizando estratégias pessoais.',2,5),
	 (32,3,'EF02MA09','Construção de sequências repetitivas e de sequências recursivas','Construir sequências de números naturais em ordem crescente ou decrescente a partir de um número qualquer, utilizando uma regularidade estabelecida.',2,6),
	 (33,3,'EF02MA10','Identificação de regularidade de sequências e determinação de elementos ausentes na sequência','Descrever um padrão (ou regularidade) de sequências repetitivas e de sequências recursivas, por meio de palavras, símbolos ou desenhos.',2,6),
	 (34,3,'EF02MA11','Identificação de regularidade de sequências e determinação de elementos ausentes na sequência','Descrever os elementos ausentes em sequências repetitivas e em sequências recursivas de números naturais, objetos ou figuras.',2,6),
	 (35,3,'EF02MA12','Localização e movimentação de pessoas e objetos no espaço, segundo pontos de referência, e indicação de mudanças de direção e sentido','Identificar e registrar, em linguagem verbal ou não verbal, a localização e os deslocamentos de pessoas e de objetos no espaço, considerando mais de um ponto de referência, e indicar as mudanças de direção e de sentido.',2,7),
	 (36,3,'EF02MA13','Esboço de roteiros e de plantas simples','Esboçar roteiros a ser seguidos ou plantas de ambientes familiares, assinalando entradas, saídas e alguns pontos de referência.',2,7),
	 (37,3,'EF02MA14','Figuras geométricas espaciais (cubo, bloco retangular, pirâmide, cone, cilindro e esfera): reconhecimento e características','Reconhecer, nomear e comparar figuras geométricas espaciais (cubo, bloco retangular, pirâmide, cone, cilindro e esfera), relacionando-as com objetos do mundo físico.',2,7),
	 (38,3,'EF02MA15','Figuras geométricas planas (círculo, quadrado, retângulo e triângulo): reconhecimento e características','Reconhecer, comparar e nomear figuras planas (círculo, quadrado, retângulo e triângulo), por meio de características comuns, em desenhos apresentados em diferentes disposições ou em sólidos geométricos.',2,7),
	 (39,3,'EF02MA16','Medida de comprimento: unidades não padronizadas e padronizadas (metro, centímetro e milímetro)','Estimar, medir e comparar comprimentos de lados de salas (incluindo contorno) e de polígonos, utilizando unidades de medida não padronizadas e padronizadas (metro, centímetro e milímetro) e instrumentos adequados.',2,8),
	 (40,3,'EF02MA17','Medida de capacidade e de massa: unidades de medida não convencionais e convencionais (litro, mililitro, cm3, grama e quilograma)','Estimar, medir e comparar capacidade e massa, utilizando estratégias pessoais e unidades de medida não padronizadas ou padronizadas (litro, mililitro, grama e quilograma).',2,8);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (41,3,'EF02MA18','Medidas de tempo: intervalo de tempo, uso do calendário, leitura de horas em relógios digitais e ordenação de datas','Indicar a duração de intervalos de tempo entre duas datas, como dias da semana e meses do ano, utilizando calendário, para planejamentos e organização de agenda.',2,8),
	 (42,3,'EF02MA19','Medidas de tempo: intervalo de tempo, uso do calendário, leitura de horas em relógios digitais e ordenação de datas','Medir a duração de um intervalo de tempo por meio de relógio digital e registrar o horário do início e do fim do intervalo.',2,8),
	 (43,3,'EF02MA20','Sistema monetário brasileiro: reconhecimento de cédulas e moedas e equivalência de valores','Estabelecer a equivalência de valores entre moedas e cédulas do sistema monetário brasileiro para resolver situações cotidianas.',2,8),
	 (44,3,'EF02MA21','Análise da ideia de aleatório em situações do cotidiano','Classificar resultados de eventos cotidianos aleatórios como pouco prováveis, muito prováveis, improváveis e impossíveis.',2,9),
	 (45,3,'EF02MA22','Coleta, classificação e representação de dados em tabelas simples e de dupla entrada e em gráficos de colunas','Comparar informações de pesquisas apresentadas por meio de tabelas de dupla entrada e em gráficos de colunas simples ou barras, para melhor compreender aspectos da realidade próxima.',2,9),
	 (46,3,'EF02MA23','Coleta, classificação e representação de dados em tabelas simples e de dupla entrada e em gráficos de colunas','Realizar pesquisa em universo de até 30 elementos, escolhendo até três variáveis categóricas de seu interesse, organizando os dados coletados em listas, tabelas e gráficos de colunas simples.',2,9),
	 (47,4,'EF03MA01','Leitura, escrita, comparação e ordenação de números naturais de quatro ordens','Ler, escrever e comparar números naturais de até a ordem de unidade de milhar, estabelecendo relações entre os registros numéricos e em língua materna.',2,5),
	 (48,4,'EF03MA02','Composição e decomposição de números naturais','Identificar características do sistema de numeração decimal, utilizando a composição e a decomposição de número natural de até quatro ordens.',2,5),
	 (49,4,'EF03MA03','Construção de fatos fundamentais da adição, subtração e multiplicação, reta numérica','Construir e utilizar fatos básicos da adição e da multiplicação para o cálculo mental ou escrito.',2,5),
	 (95,5,'EF04MA21','Áreas de figuras construídas em malhas quadriculadas','Medir, comparar e estimar área de figuras planas desenhadas em malha quadriculada, pela contagem dos quadradinhos ou de metades de quadradinho, reconhecendo que duas figuras com formatos diferentes podem ter a mesma medida de área.',2,8);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (50,4,'EF03MA04','Construção de fatos fundamentais da adição, subtração e multiplicação, reta numérica','Estabelecer a relação entre números naturais e pontos da reta numérica para utilizá-la na ordenação dos números naturais e também na construção de fatos da adição e da subtração, relacionando-os com deslocamentos para a direita ou para a esquerda.',2,5),
	 (51,4,'EF03MA05','Procedimentos de cálculo (mental e escrito) com números naturais: adição e subtração','Utilizar diferentes procedimentos de cálculo mental e escrito para resolver problemas significativos envolvendo adição e subtração com números naturais.',2,5),
	 (52,4,'EF03MA06','Problemas envolvendo significados da adição e da subtração: juntar, acrescentar, separar, retirar, comparar e completar quantidades','Resolver e elaborar problemas de adição e subtração com os significados de juntar, acrescentar, separar, retirar, comparar e completar quantidades, utilizando diferentes estratégias de cálculo exato ou aproximado, incluindo cálculo mental.',2,5),
	 (53,4,'EF03MA07','Problemas envolvendo diferentes significados da multiplicação e da divisão: adição de parcelas iguais, configuração retangular, repartição em partes iguais e medida','Resolver e elaborar problemas de multiplicação (por 2, 3, 4, 5 e 10) com os significados de adição de parcelas iguais e elementos apresentados em disposição retangular, utilizando diferentes estratégias de cálculo e registros.',2,5),
	 (54,4,'EF03MA08','Problemas envolvendo diferentes significados da multiplicação e da divisão: adição de parcelas iguais, configuração retangular, repartição em partes iguais e medida','Resolver e elaborar problemas de divisão de um número natural por outro (até 10), com resto zero e com resto diferente de zero, com os significados de repartição equitativa e de medida, por meio de estratégias e registros pessoais.',2,5),
	 (55,4,'EF03MA09','Significados de metade, terça parte, quarta parte, quinta parte e décima parte','Associar o quociente de uma divisão com resto zero de um número natural por 2, 3, 4, 5 e 10 às ideias de metade, terça, quarta, quinta e décima partes.',2,5),
	 (56,4,'EF03MA10','Identificação e descrição de regularidades em sequências numéricas recursivas','Identificar regularidades em sequências ordenadas de números naturais, resultantes da realização de adições ou subtrações sucessivas, por um mesmo número, descrever uma regra de formação da sequência e determinar elementos faltantes ou seguintes.',2,6),
	 (57,4,'EF03MA11','Relação de igualdade','Compreender a ideia de igualdade para escrever diferentes sentenças de adições ou de subtrações de dois números naturais que resultem na mesma soma ou diferença.',2,6),
	 (58,4,'EF03MA12','Localização e movimentação: representação de objetos e pontos de referência','Descrever e representar, por meio de esboços de trajetos ou utilizando croquis e maquetes, a movimentação de pessoas ou de objetos no espaço, incluindo mudanças de direção e sentido, com base em diferentes pontos de referência.',2,7),
	 (59,4,'EF03MA13','Figuras geométricas espaciais (cubo, bloco retangular, pirâmide, cone, cilindro e esfera): reconhecimento, análise de características e planificações','Associar figuras geométricas espaciais (cubo, bloco retangular, pirâmide, cone, cilindro e esfera) a objetos do mundo físico e nomear essas figuras.',2,7);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (60,4,'EF03MA14','Figuras geométricas espaciais (cubo, bloco retangular, pirâmide, cone, cilindro e esfera): reconhecimento, análise de características e planificações','Descrever características de algumas figuras geométricas espaciais (prismas retos, pirâmides, cilindros, cones), relacionando-as com suas planificações.',2,7),
	 (61,4,'EF03MA15','Figuras geométricas planas (triângulo, quadrado, retângulo, trapézio e paralelogramo): reconhecimento e análise de características','Classificar e comparar figuras planas (triângulo, quadrado, retângulo, trapézio e paralelogramo) em relação a seus lados (quantidade, posições relativas e comprimento) e vértices.',2,7),
	 (62,4,'EF03MA16','Congruência de figuras geométricas planas','Reconhecer figuras congruentes, usando sobreposição e desenhos em malhas quadriculadas ou triangulares, incluindo o uso de tecnologias digitais.',2,7),
	 (63,4,'EF03MA17','Significado de medida e de unidade de medida','Reconhecer que o resultado de uma medida depende da unidade de medida utilizada.',2,8),
	 (64,4,'EF03MA18','Significado de medida e de unidade de medida','Escolher a unidade de medida e o instrumento mais apropriado para medições de comprimento, tempo e capacidade.',2,8),
	 (65,4,'EF03MA19','Medidas de comprimento (unidades não convencionais e convencionais): registro, instrumentos de medida, estimativas e comparações','Estimar, medir e comparar comprimentos, utilizando unidades de medida não padronizadas e padronizadas mais usuais (metro, centímetro e milímetro) e diversos instrumentos de medida.',2,8),
	 (66,4,'EF03MA20','Medidas de capacidade e de massa (unidades não convencionais e convencionais): registro, estimativas e comparações','Estimar e medir capacidade e massa, utilizando unidades de medida não padronizadas e padronizadas mais usuais (litro, mililitro, quilograma, grama e miligrama), reconhecendo-as em leitura de rótulos e embalagens, entre outros.',2,8),
	 (67,4,'EF03MA21','Comparação de áreas por superposição','Comparar, visualmente ou por superposição, áreas de faces de objetos, de figuras planas ou de desenhos.',2,8),
	 (68,4,'EF03MA22','Medidas de tempo: leitura de horas em relógios digitais e analógicos, duração de eventos e reconhecimento de relações entre unidades de medida de tempo','Ler e registrar medidas e intervalos de tempo, utilizando relógios (analógico e digital) para informar os horários de início e término de realização de uma atividade e sua duração.',2,8),
	 (69,4,'EF03MA23','Medidas de tempo: leitura de horas em relógios digitais e analógicos, duração de eventos e reconhecimento de relações entre unidades de medida de tempo','Ler horas em relógios digitais e em relógios analógicos e reconhecer a relação entre hora e minutos e entre minuto e segundos.',2,8);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (70,4,'EF03MA24','Sistema monetário brasileiro: estabelecimento de equivalências de um mesmo valor na utilização de diferentes cédulas e moedas','Resolver e elaborar problemas que envolvam a comparação e a equivalência de valores monetários do sistema brasileiro em situações de compra, venda e troca.',2,8),
	 (71,4,'EF03MA25','Análise da ideia de acaso em situações do cotidiano: espaço amostral','Identificar, em eventos familiares aleatórios, todos os resultados possíveis, estimando os que têm maiores ou menores chances de ocorrência.',2,9),
	 (72,4,'EF03MA26','Leitura, interpretação e representação de dados em tabelas de dupla entrada e gráficos de barras','Resolver problemas cujos dados estão apresentados em tabelas de dupla entrada, gráficos de barras ou de colunas.',2,9),
	 (73,4,'EF03MA27','Leitura, interpretação e representação de dados em tabelas de dupla entrada e gráficos de barras','Ler, interpretar e comparar dados apresentados em tabelas de dupla entrada, gráficos de barras ou de colunas, envolvendo resultados de pesquisas significativas, utilizando termos como maior e menor frequência, apropriando-se desse tipo de linguagem para compreender aspectos da realidade sociocultural significativos.',2,9),
	 (74,4,'EF03MA28','Coleta, classificação e representação de dados referentes a variáveis categóricas, por meio de tabelas e gráficos','Realizar pesquisa envolvendo variáveis categóricas em um universo de até 50 elementos, organizar os dados coletados utilizando listas, tabelas simples ou de dupla entrada e representá-los em gráficos de colunas simples, com e sem uso de tecnologias digitais.',2,9),
	 (75,5,'EF04MA01','Sistema de numeração decimal: leitura, escrita, comparação e ordenação de números naturais de até cinco ordens','Ler, escrever e ordenar números naturais até a ordem de dezenas de milhar.',2,5),
	 (76,5,'EF04MA02','Composição e decomposição de um número natural de até cinco ordens, por meio de adições e multiplicações por potências de 10','Mostrar, por decomposição e composição, que todo número natural pode ser escrito por meio de adições e multiplicações por potências de dez, para compreender o sistema de numeração decimal e desenvolver estratégias de cálculo.',2,5),
	 (77,5,'EF04MA03','Propriedades das operações para o desenvolvimento de diferentes estratégias de cálculo com números naturais','Resolver e elaborar problemas com números naturais envolvendo adição e subtração, utilizando estratégias diversas, como cálculo, cálculo mental e algoritmos, além de fazer estimativas do resultado.',2,5),
	 (78,5,'EF04MA04','Propriedades das operações para o desenvolvimento de diferentes estratégias de cálculo com números naturais','Utilizar as relações entre adição e subtração, bem como entre multiplicação e divisão, para ampliar as estratégias de cálculo.',2,5),
	 (79,5,'EF04MA05','Propriedades das operações para o desenvolvimento de diferentes estratégias de cálculo com números naturais','Utilizar as propriedades das operações para desenvolver estratégias de cálculo.',2,5);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (80,5,'EF04MA06','Problemas envolvendo diferentes significados da multiplicação e da divisão: adição de parcelas iguais, configuração retangular, proporcionalidade, repartição equitativa e medida','Resolver e elaborar problemas envolvendo diferentes significados da multiplicação (adição de parcelas iguais, organização retangular e proporcionalidade), utilizando estratégias diversas, como cálculo por estimativa, cálculo mental e algoritmos.',2,5),
	 (81,5,'EF04MA07','Problemas envolvendo diferentes significados da multiplicação e da divisão: adição de parcelas iguais, configuração retangular, proporcionalidade, repartição equitativa e medida','Resolver e elaborar problemas de divisão cujo divisor tenha no máximo dois algarismos, envolvendo os significados de repartição equitativa e de medida, utilizando estratégias diversas, como cálculo por estimativa, cálculo mental e algoritmos.',2,5),
	 (82,5,'EF04MA08','Problemas de contagem','Resolver, com o suporte de imagem e/ou material manipulável, problemas simples de contagem, como a determinação do número de agrupamentos possíveis ao se combinar cada elemento de uma coleção com todos os elementos de outra, utilizando estratégias e formas de registro pessoais.',2,5),
	 (83,5,'EF04MA09','Números racionais: frações unitárias mais usuais (1/2, 1/3, 1/4, 1/5, 1/10 e 1/100)','Reconhecer as frações unitárias mais usuais (1/2, 1/3, 1/4, 1/5, 1/10 e 1/100) como unidades de medida menores do que uma unidade, utilizando a reta numérica como recurso.',2,5),
	 (84,5,'EF04MA10','Números racionais: representação decimal para escrever valores do sistema monetário brasileiro','Reconhecer que as regras do sistema de numeração decimal podem ser estendidas para a representação decimal de um número racional e relacionar décimos e centésimos com a representação do sistema monetário brasileiro.',2,5),
	 (85,5,'EF04MA11','Sequência numérica recursiva formada por múltiplos de um número natural','Identificar regularidades em sequências numéricas compostas por múltiplos de um número natural.',2,6),
	 (86,5,'EF04MA12','Sequência numérica recursiva formada por números que deixam o mesmo resto ao ser divididos por um mesmo número natural diferente de zero','Reconhecer, por meio de investigações, que há grupos de números naturais para os quais as divisões por um determinado número resultam em restos iguais, identificando regularidades.',2,6),
	 (87,5,'EF04MA13','Relações entre adição e subtração e entre multiplicação e divisão','Reconhecer, por meio de investigações, utilizando a calculadora quando necessário, as relações inversas entre as operações de adição e de subtração e de multiplicação e de divisão, para aplicá-las na resolução de problemas.',2,6),
	 (88,5,'EF04MA14','Propriedades da igualdade','Reconhecer e mostrar, por meio de exemplos, que a relação de igualdade existente entre dois termos permanece quando se adiciona ou se subtrai um mesmo número a cada um desses termos.',2,6),
	 (89,5,'EF04MA15','Propriedades da igualdade','Determinar o número desconhecido que torna verdadeira uma igualdade que envolve as operações fundamentais com números naturais.',2,6);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (90,5,'EF04MA16','Localização e movimentação: pontos de referência, direção e sentido, paralelismo e perpendicularismo','Descrever deslocamentos e localização de pessoas e de objetos no espaço, por meio de malhas quadriculadas e representações como desenhos, mapas, planta baixa e croquis, empregando termos como direita e esquerda, mudanças de direção e sentido, intersecção, transversais, paralelas e perpendiculares.',2,7),
	 (91,5,'EF04MA17','Figuras geométricas espaciais (prismas e pirâmides): reconhecimento, representações, planificações e características','Associar prismas e pirâmides a suas planificações e analisar, nomear e comparar seus atributos, estabelecendo relações entre as representações planas e espaciais.',2,7),
	 (92,5,'EF04MA18','Ângulos retos e não retos: uso de dobraduras, esquadros e softwares','Reconhecer ângulos retos e não retos em figuras poligonais com o uso de dobraduras, esquadros ou softwares de geometria.',2,7),
	 (93,5,'EF04MA19','Simetria de reflexão','Reconhecer simetria de reflexão em figuras e em pares de figuras geométricas planas e utilizá-la na construção de figuras congruentes, com o uso de malhas quadriculadas e de softwares de geometria.',2,7),
	 (94,5,'EF04MA20','Medidas de comprimento, massa e capacidade: estimativas, utilização de instrumentos de medida e de unidades de medida convencionais mais usuais','Medir e estimar comprimentos (incluindo perímetros), massas e capacidades, utilizando unidades de medida padronizadas mais usuais, valorizando e respeitando a cultura local.',2,8),
	 (96,5,'EF04MA22','Medidas de tempo: leitura de horas em relógios digitais e analógicos, duração de eventos e relações entre unidades de medida de tempo','Ler e registrar medidas e intervalos de tempo em horas, minutos e segundos em situações relacionadas ao seu cotidiano, como informar os horários de início e término de realização de uma tarefa e sua duração.',2,8),
	 (97,5,'EF04MA23','Medidas de temperatura em grau Celsius: construção de gráficos para indicar a variação da temperatura (mínima e máxima) medida em um dado dia ou em uma semana','Reconhecer temperatura como grandeza e o grau Celsius como unidade de medida a ela associada e utilizá-lo em comparações de temperaturas em diferentes regiões do Brasil ou no exterior ou, ainda, em discussões que envolvam problemas relacionados ao aquecimento global.',2,8),
	 (98,5,'EF04MA24','Medidas de temperatura em grau Celsius: construção de gráficos para indicar a variação da temperatura (mínima e máxima) medida em um dado dia ou em uma semana','Registrar as temperaturas máxima e mínima diárias, em locais do seu cotidiano, e elaborar gráficos de colunas com as variações diárias da temperatura, utilizando, inclusive, planilhas eletrônicas.',2,8),
	 (99,5,'EF04MA25','Problemas utilizando o sistema monetário brasileiro','Resolver e elaborar problemas que envolvam situações de compra e venda e formas de pagamento, utilizando termos como troco e desconto, enfatizando o consumo ético, consciente e responsável.',2,8),
	 (100,5,'EF04MA26','Análise de chances de eventos aleatórios','Identificar, entre eventos aleatórios cotidianos, aqueles que têm maior chance de ocorrência, reconhecendo características de resultados mais prováveis, sem utilizar frações.',2,9);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (101,5,'EF04MA27','Leitura, interpretação e representação de dados em tabelas de dupla entrada, gráficos de colunas simples e agrupadas, gráficos de barras e colunas e gráficos pictóricos','Analisar dados apresentados em tabelas simples ou de dupla entrada e em gráficos de colunas ou pictóricos, com base em informações das diferentes áreas do conhecimento, e produzir texto com a síntese de sua análise.',2,9),
	 (102,5,'EF04MA28','Diferenciação entre variáveis categóricas e variáveis numéricas, coleta, classificação e representação de dados de pesquisa realizada','Realizar pesquisa envolvendo variáveis categóricas e numéricas e organizar dados coletados por meio de tabelas e gráficos de colunas simples ou agrupadas, com e sem uso de tecnologias digitais.',2,9),
	 (103,6,'EF05MA01','Sistema de numeração decimal: leitura, escrita e ordenação de números naturais (de até seis ordens)','Ler, escrever e ordenar números naturais até a ordem das centenas de milhar com compreensão das principais características do sistema de numeração decimal.',2,5),
	 (104,6,'EF05MA02','Números racionais expressos na forma decimal e sua representação na reta numérica','Ler, escrever e ordenar números racionais na forma decimal com compreensão das principais características do sistema de numeração decimal, utilizando, como recursos, a composição e decomposição e a reta numérica.',2,5),
	 (105,6,'EF05MA03','Representação fracionária dos números racionais: reconhecimento, significados, leitura e representação na reta numérica','Identificar e representar frações (menores e maiores que a unidade), associando-as ao resultado de uma divisão ou à ideia de parte de um todo, utilizando a reta numérica como recurso.',2,5),
	 (106,6,'EF05MA04','Comparação e ordenação de números racionais na representação decimal e na fracionária utilizando a noção de equivalência','Identificar frações equivalentes.',2,5),
	 (107,6,'EF05MA05','Comparação e ordenação de números racionais na representação decimal e na fracionária utilizando a noção de equivalência','Comparar e ordenar números racionais positivos (representações fracionária e decimal), relacionando-os a pontos na reta numérica.',2,5),
	 (108,6,'EF05MA06','Cálculo de porcentagens e representação fracionária','Associar as representações 10%, 25%, 50%, 75% e 100% respectivamente à décima parte, quarta parte, metade, três quartos e um inteiro, para calcular porcentagens, utilizando estratégias pessoais, cálculo mental e calculadora, em contextos de educação financeira, entre outros.',2,5),
	 (109,6,'EF05MA07','Problemas: adição e subtração de números naturais e números racionais cuja representação decimal é finita','Resolver e elaborar problemas de adição e subtração com números naturais e com números racionais, cuja representação decimal seja finita, utilizando estratégias diversas, como cálculo por estimativa, cálculo mental e algoritmos.',2,5),
	 (110,6,'EF05MA08','Problemas: multiplicação e divisão de números racionais cuja representação decimal é finita por números naturais','Resolver e elaborar problemas de multiplicação e divisão com números naturais e com números racionais cuja representação decimal é finita (com multiplicador natural e divisor natural e diferente de zero), utilizando estratégias diversas, como cálculo por estimativa, cálculo mental e algoritmos.',2,5);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (111,6,'EF05MA09','Problemas de contagem do tipo: Se cada objeto de uma coleção A for combinado com todos os elementos de uma coleção B, quantos agrupamentos desse tipo podem ser formados?','Resolver e elaborar problemas simples de contagem envolvendo o princípio multiplicativo, como a determinação do número de agrupamentos possíveis ao se combinar cada elemento de uma coleção com todos os elementos de outra coleção, por meio de diagramas de árvore ou por tabelas.',2,5),
	 (112,6,'EF05MA10','Propriedades da igualdade e noção de equivalência','Concluir, por meio de investigações, que a relação de igualdade existente entre dois membros permanece ao adicionar, subtrair, multiplicar ou dividir cada um desses membros por um mesmo número, para construir a noção de equivalência.',2,6),
	 (113,6,'EF05MA11','Propriedades da igualdade e noção de equivalência','Resolver e elaborar problemas cuja conversão em sentença matemática seja uma igualdade com uma operação em que um dos termos é desconhecido.',2,6),
	 (114,6,'EF05MA12','Grandezas diretamente proporcionais, Problemas envolvendo a partição de um todo em duas partes proporcionais','Resolver problemas que envolvam variação de proporcionalidade direta entre duas grandezas, para associar a quantidade de um produto ao valor a pagar, alterar as quantidades de ingredientes de receitas, ampliar ou reduzir escala em mapas, entre outros.',2,6),
	 (115,6,'EF05MA13','Grandezas diretamente proporcionais, Problemas envolvendo a partição de um todo em duas partes proporcionais','Resolver problemas envolvendo a partilha de uma quantidade em duas partes desiguais, tais como dividir uma quantidade em duas partes, de modo que uma seja o dobro da outra, com compreensão da ideia de razão entre as partes e delas com o todo.',2,6),
	 (184,8,'EF07MA23','Relações entre os ângulos formados por retas paralelas intersectadas por uma transversal','Verificar relações entre os ângulos formados por retas paralelas cortadas por uma transversal, com e sem uso de softwares de geometria dinâmica.',3,7),
	 (116,6,'EF05MA14','Plano cartesiano: coordenadas cartesianas (1º quadrante) e representação de deslocamentos no plano cartesiano','Utilizar e compreender diferentes representações para a localização de objetos no plano, como mapas, células em planilhas eletrônicas e coordenadas geográficas, a fim de desenvolver as primeiras noções de coordenadas cartesianas.',2,7),
	 (117,6,'EF05MA15','Plano cartesiano: coordenadas cartesianas (1º quadrante) e representação de deslocamentos no plano cartesiano','Interpretar, descrever e representar a localização ou movimentação de objetos no plano cartesiano (1º quadrante), utilizando coordenadas cartesianas, indicando mudanças de direção e de sentido e giros.',2,7),
	 (118,6,'EF05MA16','Figuras geométricas espaciais: reconhecimento, representações, planificações e características','Associar figuras espaciais a suas planificações (prismas, pirâmides, cilindros e cones) e analisar, nomear e comparar seus atributos.',2,7),
	 (119,6,'EF05MA17','Figuras geométricas planas: características, representações e ângulos','Reconhecer, nomear e comparar polígonos, considerando lados, vértices e ângulos, e desenhá-los, utilizando material de desenho ou tecnologias digitais.',2,7);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (120,6,'EF05MA18','Ampliação e redução de figuras poligonais em malhas quadriculadas: reconhecimento da congruência dos ângulos e da proporcionalidade dos lados correspondentes','Reconhecer a congruência dos ângulos e a proporcionalidade entre os lados correspondentes de figuras poligonais em situações de ampliação e de redução em malhas quadriculadas e usando tecnologias digitais.',2,7),
	 (121,6,'EF05MA19','Medidas de comprimento, área, massa, tempo, temperatura e capacidade: utilização de unidades convencionais e relações entre as unidades de medida mais usuais','Resolver e elaborar problemas envolvendo medidas das grandezas comprimento, área, massa, tempo, temperatura e capacidade, recorrendo a transformações entre as unidades mais usuais em contextos socioculturais.',2,8),
	 (122,6,'EF05MA20','Áreas e perímetros de figuras poligonais: algumas relações','Concluir, por meio de investigações, que figuras de perímetros iguais podem ter áreas diferentes e que, também, figuras que têm a mesma área podem ter perímetros diferentes.',2,8),
	 (123,6,'EF05MA21','Noção de volume','Reconhecer volume como grandeza associada a sólidos geométricos e medir volumes por meio de empilhamento de cubos, utilizando, preferencialmente, objetos concretos.',2,8),
	 (124,6,'EF05MA22','Espaço amostral: análise de chances de eventos aleatórios','Apresentar todos os possíveis resultados de um experimento aleatório, estimando se esses resultados são igualmente prováveis ou não.',2,9),
	 (125,6,'EF05MA23','Cálculo de probabilidade de eventos equiprováveis','Determinar a probabilidade de ocorrência de um resultado em eventos aleatórios, quando todos os resultados possíveis têm a mesma chance de ocorrer (equiprováveis).',2,9),
	 (126,6,'EF05MA24','Leitura, coleta, classificação interpretação e representação de dados em tabelas de dupla entrada, gráfico de colunas agrupadas, gráficos pictóricos e gráfico de linhas','Interpretar dados estatísticos apresentados em textos, tabelas e gráficos (colunas ou linhas), referentes a outras áreas do conhecimento ou a outros contextos, como saúde e trânsito, e produzir textos com o objetivo de sintetizar conclusões.',2,9),
	 (127,6,'EF05MA25','Leitura, coleta, classificação interpretação e representação de dados em tabelas de dupla entrada, gráfico de colunas agrupadas, gráficos pictóricos e gráfico de linhas','Realizar pesquisa envolvendo variáveis categóricas e numéricas, organizar dados coletados por meio de tabelas, gráficos de colunas, pictóricos e de linhas, com e sem uso de tecnologias digitais, e apresentar texto escrito sobre a finalidade da pesquisa e a síntese dos resultados.',2,9),
	 (128,7,'EF06MA01','Sistema de numeração decimal: características, leitura, escrita e comparação de números naturais e de números racionais representados na forma decimal','Comparar, ordenar, ler e escrever números naturais e números racionais cuja representação decimal é finita, fazendo uso da reta numérica.',3,5),
	 (129,7,'EF06MA02','Sistema de numeração decimal: características, leitura, escrita e comparação de números naturais e de números racionais representados na forma decimal','Reconhecer o sistema de numeração decimal, como o que prevaleceu no mundo ocidental, e destacar semelhanças e diferenças com outros sistemas, de modo a sistematizar suas principais características (base, valor posicional e função do zero), utilizando, inclusive, a composição e decomposição de números naturais e números racionais em sua representação decimal.',3,5);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (130,7,'EF06MA03','Operações (adição, subtração, multiplicação, divisão e potenciação) com números naturais, Divisão euclidiana','Resolver e elaborar problemas que envolvam cálculos (mentais ou escritos, exatos ou aproximados) com números naturais, por meio de estratégias variadas, com compreensão dos processos neles envolvidos com e sem uso de calculadora.',3,5),
	 (131,7,'EF06MA04','Fluxograma para determinar a paridade de um número natural, Múltiplos e divisores de um número natural Números primos e compostos','Construir algoritmo em linguagem natural e representá-lo por fluxograma que indique a resolução de um problema simples (por exemplo, se um número natural qualquer é par).',3,5),
	 (132,7,'EF06MA05','Fluxograma para determinar a paridade de um número natural, Múltiplos e divisores de um número natural Números primos e compostos','Classificar números naturais em primos e compostos, estabelecer relações entre números, expressas pelos termos é múltiplo de, é divisor de, é fator de, e estabelecer, por meio de investigações, critérios de divisibilidade por 2, 3, 4, 5, 6, 8, 9, 10, 100 e 1000.',3,5),
	 (133,7,'EF06MA06','Fluxograma para determinar a paridade de um número natural, Múltiplos e divisores de um número natural Números primos e compostos','Resolver e elaborar problemas que envolvam as ideias de múltiplo e de divisor.',3,5),
	 (134,7,'EF06MA07','Frações: significados (parte/todo, quociente), equivalência, comparação, adição e subtração; cálculo da fração de um número natural; adição e subtração de frações','Compreender, comparar e ordenar frações associadas às ideias de partes de inteiros e resultado de divisão, identificando frações equivalentes.',3,5),
	 (135,7,'EF06MA08','Frações: significados (parte/todo, quociente), equivalência, comparação, adição e subtração; cálculo da fração de um número natural; adição e subtração de frações','Reconhecer que os números racionais positivos podem ser expressos nas formas fracionária e decimal, estabelecer relações entre essas representações, passando de uma representação para outra, e relacioná-los a pontos na reta numérica.',3,5),
	 (211,9,'EF08MA13','Variação de grandezas: diretamente proporcionais, inversamente proporcionais ou não proporcionais','Resolver e elaborar problemas que envolvam grandezas diretamente ou inversamente proporcionais, por meio de estratégias variadas.',3,6),
	 (136,7,'EF06MA09','Frações: significados (parte/todo, quociente), equivalência, comparação, adição e subtração; cálculo da fração de um número natural; adição e subtração de frações','Resolver e elaborar problemas que envolvam o cálculo da fração de uma quantidade e cujo resultado seja um número natural, com e sem uso de calculadora.',3,5),
	 (137,7,'EF06MA10','Frações: significados (parte/todo, quociente), equivalência, comparação, adição e subtração; cálculo da fração de um número natural; adição e subtração de frações','Resolver e elaborar problemas que envolvam adição ou subtração com números racionais positivos na representação fracionária.',3,5),
	 (138,7,'EF06MA11','Operações (adição, subtração, multiplicação, divisão e potenciação) com números racionais','Resolver e elaborar problemas com números racionais positivos na representação decimal, envolvendo as quatro operações fundamentais e a potenciação, por meio de estratégias diversas, utilizando estimativas e arredondamentos para verificar a razoabilidade de respostas, com e sem uso de calculadora.',3,5);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (139,7,'EF06MA12','Aproximação de números para múltiplos de potências de 10','Fazer estimativas de quantidades e aproximar números para múltiplos da potência de 10 mais próxima.',3,5),
	 (140,7,'EF06MA13','Cálculo de porcentagens por meio de estratégias diversas, sem fazer uso da regra de três','Resolver e elaborar problemas que envolvam porcentagens, com base na ideia de proporcionalidade, sem fazer uso da regra de três, utilizando estratégias pessoais, cálculo mental e calculadora, em contextos de educação financeira, entre outros.',3,5),
	 (141,7,'EF06MA14','Propriedades da igualdade','Reconhecer que a relação de igualdade matemática não se altera ao adicionar, subtrair, multiplicar ou dividir os seus dois membros por um mesmo número e utilizar essa noção para determinar valores desconhecidos na resolução de problemas.',3,6),
	 (142,7,'EF06MA15','Problemas que tratam da partição de um todo em duas partes desiguais, envolvendo razões entre as partes e entre uma das partes e o todo','Resolver e elaborar problemas que envolvam a partilha de uma quantidade em duas partes desiguais, envolvendo relações aditivas e multiplicativas, bem como a razão entre as partes e entre uma das partes e o todo.',3,6),
	 (143,7,'EF06MA16','Plano cartesiano: associação dos vértices de um polígono a pares ordenados','Associar pares ordenados de números a pontos do plano cartesiano do 1º quadrante, em situações como a localização dos vértices de um polígono.',3,7),
	 (144,7,'EF06MA17','Prismas e pirâmides: planificações e relações entre seus elementos (vértices, faces e arestas)','Quantificar e estabelecer relações entre o número de vértices, faces e arestas de prismas e pirâmides, em função do seu polígono da base, para resolver problemas e desenvolver a percepção espacial.',3,7),
	 (145,7,'EF06MA18','Polígonos: classificações quanto ao número de vértices, às medidas de lados e ângulos e ao paralelismo e perpendicularismo dos lados','Reconhecer, nomear e comparar polígonos, considerando lados, vértices e ângulos, e classificá-los em regulares e não regulares, tanto em suas representações no plano como em faces de poliedros.',3,7),
	 (146,7,'EF06MA19','Polígonos: classificações quanto ao número de vértices, às medidas de lados e ângulos e ao paralelismo e perpendicularismo dos lados','Identificar características dos triângulos e classificá-los em relação às medidas dos lados e dos ângulos.',3,7),
	 (147,7,'EF06MA20','Polígonos: classificações quanto ao número de vértices, às medidas de lados e ângulos e ao paralelismo e perpendicularismo dos lados','Identificar características dos quadriláteros, classificá-los em relação a lados e a ângulos e reconhecer a inclusão e a intersecção de classes entre eles.',3,7),
	 (148,7,'EF06MA21','Construção de figuras semelhantes: ampliação e redução de figuras planas em malhas quadriculadas','Construir figuras planas semelhantes em situações de ampliação e de redução, com o uso de malhas quadriculadas, plano cartesiano ou tecnologias digitais.',3,7);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (149,7,'EF06MA22','Construção de retas paralelas e perpendiculares, fazendo uso de réguas, esquadros e softwares','Utilizar instrumentos, como réguas e esquadros, ou softwares para representações de retas paralelas e perpendiculares e construção de quadriláteros, entre outros.',3,7),
	 (150,7,'EF06MA23','Construção de retas paralelas e perpendiculares, fazendo uso de réguas, esquadros e softwares','Construir algoritmo para resolver situações passo a passo (como na construção de dobraduras ou na indicação de deslocamento de um objeto no plano segundo pontos de referência e distâncias fornecidas etc.).',3,7),
	 (151,7,'EF06MA24','Problemas sobre medidas envolvendo grandezas como comprimento, massa, tempo, temperatura, área, capacidade e volume','Resolver e elaborar problemas que envolvam as grandezas comprimento, massa, tempo, temperatura, área (triângulos e retângulos), capacidade e volume (sólidos formados por blocos retangulares), sem uso de fórmulas, inseridos, sempre que possível, em contextos oriundos de situações reais e/ou relacionadas às outras áreas do conhecimento.',3,8),
	 (152,7,'EF06MA25','Ângulos: noção, usos e medida','Reconhecer a abertura do ângulo como grandeza associada às figuras geométricas.',3,8),
	 (153,7,'EF06MA26','Ângulos: noção, usos e medida','Resolver problemas que envolvam a noção de ângulo em diferentes contextos e em situações reais, como ângulo de visão.',3,8),
	 (154,7,'EF06MA27','Ângulos: noção, usos e medida','Determinar medidas da abertura de ângulos, por meio de transferidor e/ou tecnologias digitais.',3,8),
	 (155,7,'EF06MA28','Plantas baixas e vistas aéreas','Interpretar, descrever e desenhar plantas baixas simples de residências e vistas aéreas.',3,8),
	 (156,7,'EF06MA29','Perímetro de um quadrado como grandeza proporcional à medida do lado','Analisar e descrever mudanças que ocorrem no perímetro e na área de um quadrado ao se ampliarem ou reduzirem, igualmente, as medidas de seus lados, para compreender que o perímetro é proporcional à medida do lado, o que não ocorre com a área.',3,8),
	 (157,7,'EF06MA30','Cálculo de probabilidade como a razão entre o número de resultados favoráveis e o total de resultados possíveis em um espaço amostral equiprovável, Cálculo de probabilidade por meio de muitas repetições de um experimento (frequências de ocorrências e probabilidade frequentista)','Calcular a probabilidade de um evento aleatório, expressando-a por número racional (forma fracionária, decimal e percentual) e comparar esse número com a probabilidade obtida por meio de experimentos sucessivos.',3,9),
	 (158,7,'EF06MA31','Leitura e interpretação de tabelas e gráficos (de colunas ou barras simples ou múltiplas) referentes a variáveis categóricas e variáveis numéricas','Identificar as variáveis e suas frequências e os elementos constitutivos (título, eixos, legendas, fontes e datas) em diferentes tipos de gráfico.',3,9);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (159,7,'EF06MA32','Leitura e interpretação de tabelas e gráficos (de colunas ou barras simples ou múltiplas) referentes a variáveis categóricas e variáveis numéricas','Interpretar e resolver situações que envolvam dados de pesquisas sobre contextos ambientais, sustentabilidade, trânsito, consumo responsável, entre outros, apresentadas pela mídia em tabelas e em diferentes tipos de gráficos e redigir textos escritos com o objetivo de sintetizar conclusões.',3,9),
	 (160,7,'EF06MA33','Coleta de dados, organização e registro, Construção de diferentes tipos de gráficos para representá-los e interpretação das informações','Planejar e coletar dados de pesquisa referente a práticas sociais escolhidas pelos alunos e fazer uso de planilhas eletrônicas para registro, representação e interpretação das informações, em tabelas, vários tipos de gráficos e texto.',3,9),
	 (161,7,'EF06MA34','Diferentes tipos de representação de informações: gráficos e fluxogramas','Interpretar e desenvolver fluxogramas simples, identificando as relações entre os objetos representados (por exemplo, posição de cidades considerando as estradas que as unem, hierarquia dos funcionários de uma empresa etc.).',3,9),
	 (162,8,'EF07MA01','Múltiplos e divisores de um número natural','Resolver e elaborar problemas com números naturais, envolvendo as noções de divisor e de múltiplo, podendo incluir máximo divisor comum ou mínimo múltiplo comum, por meio de estratégias diversas, sem a aplicação de algoritmos.',3,5),
	 (163,8,'EF07MA02','Cálculo de porcentagens e de acréscimos e decréscimos simples','Resolver e elaborar problemas que envolvam porcentagens, como os que lidam com acréscimos e decréscimos simples, utilizando estratégias pessoais, cálculo mental e calculadora, no contexto de educação financeira, entre outros.',3,5),
	 (164,8,'EF07MA03','Números inteiros: usos, história, ordenação, associação com pontos da reta numérica e operações','Comparar e ordenar números inteiros em diferentes contextos, incluindo o histórico, associá-los a pontos da reta numérica e utilizá-los em situações que envolvam adição e subtração.',3,5),
	 (165,8,'EF07MA04','Números inteiros: usos, história, ordenação, associação com pontos da reta numérica e operações','Resolver e elaborar problemas que envolvam operações com números inteiros.',3,5),
	 (166,8,'EF07MA05','Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador','Resolver um mesmo problema utilizando diferentes algoritmos.',3,5),
	 (167,8,'EF07MA06','Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador','Reconhecer que as resoluções de um grupo de problemas que têm a mesma estrutura podem ser obtidas utilizando os mesmos procedimentos.',3,5),
	 (168,8,'EF07MA07','Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador','Representar por meio de um fluxograma os passos utilizados para resolver um grupo de problemas.',3,5);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (169,8,'EF07MA08','Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador','Comparar e ordenar frações associadas às ideias de partes de inteiros, resultado da divisão, razão e operador.',3,5),
	 (170,8,'EF07MA09','Fração e seus significados: como parte de inteiros, resultado da divisão, razão e operador','Utilizar, na resolução de problemas, a associação entre razão e fração, como a fração 2/3 para expressar a razão de duas partes de uma grandeza para três partes da mesma ou três partes de outra grandeza.',3,5),
	 (171,8,'EF07MA10','Números racionais na representação fracionária e na decimal: usos, ordenação e associação com pontos da reta numérica e operações','Comparar e ordenar números racionais em diferentes contextos e associá-los a pontos da reta numérica.',3,5),
	 (172,8,'EF07MA11','Números racionais na representação fracionária e na decimal: usos, ordenação e associação com pontos da reta numérica e operações','Compreender e utilizar a multiplicação e a divisão de números racionais, a relação entre elas e suas propriedades operatórias.',3,5),
	 (173,8,'EF07MA12','Números racionais na representação fracionária e na decimal: usos, ordenação e associação com pontos da reta numérica e operações','Resolver e elaborar problemas que envolvam as operações com números racionais.',3,5),
	 (174,8,'EF07MA13','Linguagem algébrica: variável e incógnita','Compreender a ideia de variável, representada por letra ou símbolo, para expressar relação entre duas grandezas, diferenciando-a da ideia de incógnita.',3,6),
	 (175,8,'EF07MA14','Linguagem algébrica: variável e incógnita','Classificar sequências em recursivas e não recursivas, reconhecendo que o conceito de recursão está presente não apenas na matemática, mas também nas artes e na literatura.',3,6),
	 (176,8,'EF07MA15','Linguagem algébrica: variável e incógnita','Utilizar a simbologia algébrica para expressar regularidades encontradas em sequências numéricas.',3,6),
	 (177,8,'EF07MA16','Equivalência de expressões algébricas: identificação da regularidade de uma sequência numérica','Reconhecer se duas expressões algébricas obtidas para descrever a regularidade de uma mesma sequência numérica são ou não equivalentes.',3,6),
	 (178,8,'EF07MA17','Problemas envolvendo grandezas diretamente proporcionais e grandezas inversamente proporcionais','Resolver e elaborar problemas que envolvam variação de proporcionalidade direta e de proporcionalidade inversa entre duas grandezas, utilizando sentença algébrica para expressar a relação entre elas.',3,6);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (179,8,'EF07MA18','Equações polinomiais do 1º grau','Resolver e elaborar problemas que possam ser representados por equações polinomiais de 1º grau, redutíveis à forma ax + b = c, fazendo uso das propriedades da igualdade.',3,6),
	 (180,8,'EF07MA19','Transformações geométricas de polígonos no plano cartesiano: multiplicação das coordenadas por um número inteiro e obtenção de simétricos em relação aos eixos e à origem','Realizar transformações de polígonos representados no plano cartesiano, decorrentes da multiplicação das coordenadas de seus vértices por um número inteiro.',3,7),
	 (181,8,'EF07MA20','Transformações geométricas de polígonos no plano cartesiano: multiplicação das coordenadas por um número inteiro e obtenção de simétricos em relação aos eixos e à origem','Reconhecer e representar, no plano cartesiano, o simétrico de figuras em relação aos eixos e à origem.',3,7),
	 (182,8,'EF07MA21','Simetrias de translação, rotação e reflexão','Reconhecer e construir figuras obtidas por simetrias de translação, rotação e reflexão, usando instrumentos de desenho ou softwares de geometria dinâmica e vincular esse estudo a representações planas de obras de arte, elementos arquitetônicos, entre outros.',3,7),
	 (183,8,'EF07MA22','A circunferência como lugar geométrico','Construir circunferências, utilizando compasso, reconhecê-las como lugar geométrico e utilizá-las para fazer composições artísticas e resolver problemas que envolvam objetos equidistantes.',3,7),
	 (185,8,'EF07MA24','Triângulos: construção, condição de existência e soma das medidas dos ângulos internos','Construir triângulos, usando régua e compasso, reconhecer a condição de existência do triângulo quanto à medida dos lados e verificar que a soma das medidas dos ângulos internos de um triângulo é 180°.',3,7),
	 (186,8,'EF07MA25','Triângulos: construção, condição de existência e soma das medidas dos ângulos internos','Reconhecer a rigidez geométrica dos triângulos e suas aplicações, como na construção de estruturas arquitetônicas (telhados, estruturas metálicas e outras) ou nas artes plásticas.',3,7),
	 (187,8,'EF07MA26','Triângulos: construção, condição de existência e soma das medidas dos ângulos internos','Descrever, por escrito e por meio de um fluxograma, um algoritmo para a construção de um triângulo qualquer, conhecidas as medidas dos três lados.',3,7),
	 (188,8,'EF07MA27','Polígonos regulares: quadrado e triângulo equilátero','Calcular medidas de ângulos internos de polígonos regulares, sem o uso de fórmulas, e estabelecer relações entre ângulos internos e externos de polígonos, preferencialmente vinculadas à construção de mosaicos e de ladrilhamentos.',3,7),
	 (189,8,'EF07MA28','Polígonos regulares: quadrado e triângulo equilátero','Descrever, por escrito e por meio de um fluxograma, um algoritmo para a construção de um polígono regular (como quadrado e triângulo equilátero), conhecida a medida de seu lado.',3,7);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (190,8,'EF07MA29','Problemas envolvendo medições','Resolver e elaborar problemas que envolvam medidas de grandezas inseridos em contextos oriundos de situações cotidianas ou de outras áreas do conhecimento, reconhecendo que toda medida empírica é aproximada.',3,8),
	 (191,8,'EF07MA30','Cálculo de volume de blocos retangulares, utilizando unidades de medida convencionais mais usuais','Resolver e elaborar problemas de cálculo de medida do volume de blocos retangulares, envolvendo as unidades usuais (metro cúbico, decímetro cúbico e centímetro cúbico).',3,8),
	 (192,8,'EF07MA31','Equivalência de área de figuras planas: cálculo de áreas de figuras que podem ser decompostas por outras, cujas áreas podem ser facilmente determinadas como triângulos e quadriláteros','Estabelecer expressões de cálculo de área de triângulos e de quadriláteros.',3,8),
	 (193,8,'EF07MA32','Equivalência de área de figuras planas: cálculo de áreas de figuras que podem ser decompostas por outras, cujas áreas podem ser facilmente determinadas como triângulos e quadriláteros','Resolver e elaborar problemas de cálculo de medida de área de figuras planas que podem ser decompostas por quadrados, retângulos e/ou triângulos, utilizando a equivalência entre áreas.',3,8),
	 (194,8,'EF07MA33','Medida do comprimento da circunferência','Estabelecer o número π como a razão entre a medida de uma circunferência e seu diâmetro, para compreender e resolver problemas, inclusive os de natureza histórica.',3,8),
	 (195,8,'EF07MA34','Experimentos aleatórios: espaço amostral e estimativa de probabilidade por meio de frequência de ocorrências','Planejar e realizar experimentos aleatórios ou simulações que envolvem cálculo de probabilidades ou estimativas por meio de frequência de ocorrências.',3,9),
	 (196,8,'EF07MA35','Estatística: média e amplitude de um conjunto de dados','Compreender, em contextos significativos, o significado de média estatística como indicador da tendência de uma pesquisa, calcular seu valor e relacioná-lo, intuitivamente, com a amplitude do conjunto de dados.',3,9),
	 (197,8,'EF07MA36','Pesquisa amostral e pesquisa censitária, Planejamento de pesquisa, coleta e organização dos dados, construção de tabelas e gráficos e interpretação das informações','Planejar e realizar pesquisa envolvendo tema da realidade social, identificando a necessidade de ser censitária ou de usar amostra, e interpretar os dados para comunicá-los por meio de relatório escrito, tabelas e gráficos, com o apoio de planilhas eletrônicas.',3,9),
	 (198,8,'EF07MA37','Gráficos de setores: interpretação, pertinência e construção para representar conjunto de dados','Interpretar e analisar dados apresentados em gráfico de setores divulgados pela mídia e compreender quando é possível ou conveniente sua utilização.',3,9),
	 (199,9,'EF08MA01','Notação científica','Efetuar cálculos com potências de expoentes inteiros e aplicar esse conhecimento na representação de números em notação científica.',3,5);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (200,9,'EF08MA02','Potenciação e radiciação','Resolver e elaborar problemas usando a relação entre potenciação e radiciação, para representar uma raiz como potência de expoente fracionário.',3,5),
	 (201,9,'EF08MA03','O princípio multiplicativo da contagem','Resolver e elaborar problemas de contagem cuja resolução envolva a aplicação do princípio multiplicativo.',3,5),
	 (202,9,'EF08MA04','Porcentagens','Resolver e elaborar problemas, envolvendo cálculo de porcentagens, incluindo o uso de tecnologias digitais.',3,5),
	 (203,9,'EF08MA05','Dízimas periódicas: fração geratriz','Reconhecer e utilizar procedimentos para a obtenção de uma fração geratriz para uma dízima periódica.',3,5),
	 (204,9,'EF08MA06','Valor numérico de expressões algébricas','Resolver e elaborar problemas que envolvam cálculo do valor numérico de expressões algébricas, utilizando as propriedades das operações.',3,6),
	 (205,9,'EF08MA07','Associação de uma equação linear de 1º grau a uma reta no plano cartesiano','Associar uma equação linear de 1º grau com duas incógnitas a uma reta no plano cartesiano.',3,6),
	 (206,9,'EF08MA08','Sistema de equações polinomiais de 1º grau: resolução algébrica e representação no plano cartesiano','Resolver e elaborar problemas relacionados ao seu contexto próximo, que possam ser representados por sistemas de equações de 1º grau com duas incógnitas e interpretá-los, utilizando, inclusive, o plano cartesiano como recurso.',3,6),
	 (207,9,'EF08MA09','Equação polinomial de 2º grau do tipo ax2 = b','Resolver e elaborar, com e sem uso de tecnologias, problemas que possam ser representados por equações polinomiais de 2º grau do tipo ax2 = b.',3,6),
	 (208,9,'EF08MA10','Sequências recursivas e não recursivas','Identificar a regularidade de uma sequência numérica ou figural não recursiva e construir um algoritmo por meio de um fluxograma que permita indicar os números ou as figuras seguintes.',3,6),
	 (209,9,'EF08MA11','Sequências recursivas e não recursivas','Identificar a regularidade de uma sequência numérica recursiva e construir um algoritmo por meio de um fluxograma que permita indicar os números seguintes.',3,6);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (210,9,'EF08MA12','Variação de grandezas: diretamente proporcionais, inversamente proporcionais ou não proporcionais','Identificar a natureza da variação de duas grandezas, diretamente, inversamente proporcionais ou não proporcionais, expressando a relação existente por meio de sentença algébrica e representá-la no plano cartesiano.',3,6),
	 (212,9,'EF08MA14','Congruência de triângulos e demonstrações de propriedades de quadriláteros','Demonstrar propriedades de quadriláteros por meio da identificação da congruência de triângulos.',3,7),
	 (213,9,'EF08MA15','Construções geométricas: ângulos de 90°, 60°, 45° e 30° e polígonos regulares','Construir, utilizando instrumentos de desenho ou softwares de geometria dinâmica, mediatriz, bissetriz, ângulos de 90°, 60°, 45° e 30° e polígonos regulares.',3,7),
	 (214,9,'EF08MA16','Construções geométricas: ângulos de 90°, 60°, 45° e 30° e polígonos regulares','Descrever, por escrito e por meio de um fluxograma, um algoritmo para a construção de um hexágono regular de qualquer área, a partir da medida do ângulo central e da utilização de esquadros e compasso.',3,7),
	 (215,9,'EF08MA17','Mediatriz e bissetriz como lugares geométricos: construção e problemas','Aplicar os conceitos de mediatriz e bissetriz como lugares geométricos na resolução de problemas.',3,7),
	 (216,9,'EF08MA18','Transformações geométricas: simetrias de translação, reflexão e rotação','Reconhecer e construir figuras obtidas por composições de transformações geométricas (translação, reflexão e rotação), com o uso de instrumentos de desenho ou de softwares de geometria dinâmica.',3,7),
	 (217,9,'EF08MA19','Área de figuras planas, Área do círculo e comprimento de sua circunferência','Resolver e elaborar problemas que envolvam medidas de área de figuras geométricas, utilizando expressões de cálculo de área (quadriláteros, triângulos e círculos), em situações como determinar medida de terrenos.',3,8),
	 (218,9,'EF08MA20','Volume de bloco retangular, Medidas de capacidade','Reconhecer a relação entre um litro e um decímetro cúbico e a relação entre litro e metro cúbico, para resolver problemas de cálculo de capacidade de recipientes.',3,8),
	 (219,9,'EF08MA21','Volume de bloco retangular, Medidas de capacidade','Resolver e elaborar problemas que envolvam o cálculo do volume de recipiente cujo formato é o de um bloco retangular.',3,8),
	 (220,9,'EF08MA22','Princípio multiplicativo da contagem, Soma das probabilidades de todos os elementos de um espaço amostral','Calcular a probabilidade de eventos, com base na construção do espaço amostral, utilizando o princípio multiplicativo, e reconhecer que a soma das probabilidades de todos os elementos do espaço amostral é igual a 1.',3,9);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (221,9,'EF08MA23','Gráficos de barras, colunas, linhas ou setores e seus elementos constitutivos e adequação para determinado conjunto de dados','Avaliar a adequação de diferentes tipos de gráficos para representar um conjunto de dados de uma pesquisa.',3,9),
	 (222,9,'EF08MA24','Organização dos dados de uma variável contínua em classes','Classificar as frequências de uma variável contínua de uma pesquisa em classes, de modo que resumam os dados de maneira adequada para a tomada de decisões.',3,9),
	 (223,9,'EF08MA25','Medidas de tendência central e de dispersão','Obter os valores de medidas de tendência central de uma pesquisa estatística (média, moda e mediana) com a compreensão de seus significados e relacioná-los com a dispersão de dados, indicada pela amplitude.',3,9),
	 (224,9,'EF08MA26','Pesquisas censitária ou amostral, Planejamento e execução de pesquisa amostral','Selecionar razões, de diferentes naturezas (física, ética ou econômica), que justificam a realização de pesquisas amostrais e não censitárias, e reconhecer que a seleção da amostra pode ser feita de diferentes maneiras (amostra casual simples, sistemática e estratificada).',3,9),
	 (225,9,'EF08MA27','Pesquisas censitária ou amostral, Planejamento e execução de pesquisa amostral','Planejar e executar pesquisa amostral, selecionando uma técnica de amostragem adequada, e escrever relatório que contenha os gráficos apropriados para representar os conjuntos de dados, destacando aspectos como as medidas de tendência central, a amplitude e as conclusões.',3,9),
	 (226,10,'EF09MA01','Necessidade dos números reais para medir qualquer segmento de reta, Números irracionais: reconhecimento e localização de alguns na reta numérica','Reconhecer que, uma vez fixada uma unidade de comprimento, existem segmentos de reta cujo comprimento não é expresso por número racional (como as medidas de diagonais de um polígono e alturas de um triângulo, quando se toma a medida de cada lado como unidade).',3,5),
	 (227,10,'EF09MA02','Necessidade dos números reais para medir qualquer segmento de reta, Números irracionais: reconhecimento e localização de alguns na reta numérica','Reconhecer um número irracional como um número real cuja representação decimal é infinita e não periódica, e estimar a localização de alguns deles na reta numérica.',3,5),
	 (228,10,'EF09MA03','Potências com expoentes negativos e fracionários','Efetuar cálculos com números reais, inclusive potências com expoentes fracionários.',3,5),
	 (229,10,'EF09MA04','Números reais: notação científica e problemas','Resolver e elaborar problemas com números reais, inclusive em notação científica, envolvendo diferentes operações.',3,5),
	 (230,10,'EF09MA05','Porcentagens: problemas que envolvem cálculo de percentuais sucessivos','Resolver e elaborar problemas que envolvam porcentagens, com a ideia de aplicação de percentuais sucessivos e a determinação das taxas percentuais, preferencialmente com o uso de tecnologias digitais, no contexto da educação financeira.',3,5);
INSERT INTO public.habilidade (id,ano_ensino_id,codigo,conhecimentos,descricao,nivel_ensino_id,tema_conteudo_id) VALUES
	 (231,10,'EF09MA06','Funções: representações numérica, algébrica e gráfica','Compreender as funções como relações de dependência unívoca entre duas variáveis e suas representações numérica, algébrica e gráfica e utilizar esse conceito para analisar situações que envolvam relações funcionais entre duas variáveis.',3,6),
	 (232,10,'EF09MA07','Razão entre grandezas de espécies diferentes','Resolver problemas que envolvam a razão entre duas grandezas de espécies diferentes, como velocidade e densidade demográfica.',3,6),
	 (233,10,'EF09MA08','Grandezas diretamente proporcionais e grandezas inversamente proporcionais','Resolver e elaborar problemas que envolvam relações de proporcionalidade direta e inversa entre duas ou mais grandezas, inclusive escalas, divisão em partes proporcionais e taxa de variação, em contextos socioculturais, ambientais e de outras áreas.',3,6),
	 (234,10,'EF09MA09','Expressões algébricas: fatoração e produtos notáveis, Resolução de equações polinomiais do 2º grau por meio de fatorações','Compreender os processos de fatoração de expressões algébricas, com base em suas relações com os produtos notáveis, para resolver e elaborar problemas que possam ser representados por equações polinomiais do 2º grau.',3,6),
